import React, { Component } from 'react';

class Article extends Component {
  render() {
    const { title, created_date: createdDate, abstract, byline, image } = this.props;
    return (
      <div>
        <h1>{ title }</h1>
        <p>{ createdDate }</p>
        { byline && <h2>{ byline }</h2> }
        { image && <img src={ image } alt=""/> }
        <p>{ abstract }</p>
      </div>
    )
  }
}

export default Article;


